mapper.select:
SELECT

FROM GLCS_OPERATE_LOG P,GLCS_STAFF S,GLCS_OPERATE_TYPE L

	P.STAFF_ID = S.STAFF_ID AND P.OPERATE_TYPE = L.OPERATE_TYPE
	and P.OPERATE_TYPE not in
	(114,115,116,117,118,119,120,123,124,127,128,129,130,131,132,133,134,135,136,137,138,139,140,141,142)

		AND S.NAME LIKE '%'|| #{bean.model.name} ||'%'


		AND P.SOURCE = #{bean.model.source}


		AND P.MENU like '%'|| #{bean.model.menu} ||'%'


	  	AND P.START_DATE >= TO_DATE(#{bean.queryStartDateStr},'yyyy-MM-dd hh24:mi:ss')


	  	AND P.END_DATE <= TO_DATE(#{bean.queryEndDateStr},'yyyy-MM-dd hh24:mi:ss')


		AND L.OPERATE_TYPE = #{bean.model.pubOperateType.operateTypeName}


		AND P.OPERATE_STATUS = #{bean.model.operateStatus}




	${bean.sort} ${bean.dir}

SELECT
FROM GLCS_OPERATE_LOG P JOIN GLCS_STAFF S ON P.STAFF_ID = S.STAFF_ID JOIN GLCS_OPERATE_TYPE L ON P.OPERATE_TYPE = L.OPERATE_TYPE left join PHY_EQUIPMENT E on E.PHY_EQP_ID = P.PHY_EQP_ID
P.OPERATE_TYPE in (114,115,116,117,118,119,120,123,124,127,128,129,130,131,132,133,134,135,136,137,138,139,140,141,142)
AND S.NAME LIKE '%'|| #{bean.model.name} ||'%'
AND P.SOURCE = #{bean.model.source}
AND P.MENU like '%'|| #{bean.model.menu} ||'%'
AND P.START_DATE >= TO_DATE(#{bean.queryStartDateStr},'yyyy-MM-dd hh24:mi:ss')
AND P.END_DATE <= TO_DATE(#{bean.queryEndDateStr},'yyyy-MM-dd hh24:mi:ss')
AND L.OPERATE_TYPE = #{bean.model.pubOperateType.operateTypeName}
AND P.OPERATE_STATUS = #{bean.model.operateStatus}
${bean.sort} ${bean.dir}
